sys_script.script:
(function executeRule(current, previous /*, g*/) {
    // Get the values of the fields you want to check for duplicates
    var movingTimeValue = current.moving_time; 
    var elevationGainValue = current.total_elevation_gain;
	var athleteValue = current.athlete;

    // Query the table to check for existing records with the same values
    var existingRecord = new GlideRecord('x_snc_nighthawks_0_team_activity');
    existingRecord.addQuery('moving_time', movingTimeValue);
    existingRecord.addQuery('total_elevation_gain', elevationGainValue);
	existingRecord.addQuery('athlete', athleteValue);
    existingRecord.query();

    // If a record with the same values exists, prevent insertion
    if (existingRecord.next()) {
        gs.addErrorMessage("Duplicate record found. Please check Moving Time and Total Elevation Gain values.");
        current.setAbortAction(true); // Abort the insertion
    }
})(current, previous);
